function firstMarkerAfter($text, $chunkSize)
{
    $length = strlen($text);
    $pos = $chunkSize;
    do {
        $chunk = substr($text, $pos-$chunkSize, $chunkSize);
        $ok = collect(str_split($chunk))->unique()->count() == $chunkSize;
        if ($ok) {
            return $pos;
        }
        $pos++;
    } while ($pos < $length);
    return -1;
}


